import { useState } from 'react'
import './app.css'

export default function App(){

  /* estados (ou useState) é algo mutável, ou seja, pode trocar de valor conforme a minha aplicação sem que eu tenha que recarregar a página. Então ele salva esses valores como se fosse uma variável para mim ter acesso depois */
  const [peso, setPeso] = useState('');
  const [altura, setAltura] = useState('');


  function calcularIMC(){
    const alt = altura / 100;
    const imc = peso / (alt * alt);

    const [mensagem, setMensagem] = useState('');


    if(imc < 18.6){
      alert("Voce está abaixo do peso! Seu IMC: " +imc.toFixed(1))
    }else if(imc >= 18.6 && imc < 24.9){
      alert("Peso ideal! Seu IMC: " +imc.toFixed(1))
    }else if(imc >= 24.9 && imc < 34.9){
      alert("Voce está levemente acima do peso! Seu IMC: " +imc.toFixed(1))
    }else if(imc > 34.9){
      alert("Cuidado Obesidade! Seu IMC: " +imc.toFixed(1))
    }
  }


  return(
    <div className="app">
      <h1>Calculadora IMC</h1>
      <span>Vamos calcular seu imc</span>

      <div className="area-input">
        <input 
          type="text" 
          placeholder="Peso em (kg) Ex: 70"
          value={peso}
          onChange={(e) => setPeso(e.target.value)}
        />
        <input 
          type="text" 
          placeholder="Altura em (cm) Ex: 176"
          value={altura}
          onChange={(e) => setAltura(e.target.value)}
        />

        <button onClick={calcularIMC}>
          Calcular
        </button>
      </div>

      <h2>Seu imc foi 25, você está abaixo do peso!</h2>
    </div>
  )
}
